export class validation {
    constructor(rules) {
        this.rules = rules;
    }
    validate(formdata) {
        let is_valid = true;
        let result = {};
        Object.keys(this.rules).map((n, key) => {
            const rules = this.rules[n].split('|');
            if (rules.includes('required') && !formdata[n]) {
