                }
            }
        },
        fetchPosts(force = false) {
            let params = this.generateParams();
            const posts = this.post[this.filter] || {};
            if (!posts.status || force)
                this.$store.dispatch(TYPES.GET_POSTS, params);
        },
        morePosts() {
