                    is_valid: false,
                    message: 'this input is required'
                };
            }
            else {
                is_valid = is_valid && true;
                result[n] = {
                    is_valid: true,
                    message: ''
                };
            }
        });
        if (!is_valid)
            toast('form belum valid', 'error');
        return {
            is_valid,
            result
        };
    }
}



